
public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=200;
		int b=300;
		System.out.println("Before swapping");
		System.out.println("a= "+ " "+a+" "+ "b= "+b);
		
		int temp=0;
		temp=a;
		a=b;
		b=temp;
		System.out.println("After swapping");
		System.out.println("a= "+ " "+a+" "+ "b= "+b);
		
	}

}
