/*even and odd using for loop*/
package com.loop;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for (i=0;i<10;i++){
			if (i%2==0){
			System.out.println("Even number: "+ i);
			}
			else{
			System.out.println("Odd number: "+ i);
			}
		}
	}

}
