package com.shift;

public class RightShiftEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// '>>' is used to move the value of the left operand to right by the number of its specified by right operand

		System.out.println(20 >> 2); // 20/2^2 =20/4(2*2)=5
		System.out.println(10 >> 5); // 10/2^5=10/32 (2*2*2*2*2)=0
	}

}
