/*
 Ternary Operator
 () ? st1 : st2 ;

if condition is true after question mark symbol st1 will be return

if condition is false after colon(:) st2 will be return
 */
package com.ternary;

public class TernaryEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =1;
		int b=20;
		
		String msg=(a<b)?"Valid":"Invalid";
		System.out.println(msg);
	}

}
